import { APP_ACTION_ADD } from "../Constants"

//rxreducer ninja
const initialState = {

    count:0,
    account: {username:'admin',password:'1234'},

}

export default (state = initialState, { type, payload }) => {
    switch (type) {
        case APP_ACTION_ADD:
            return {...state,count:state.count+1}
    default:
        return state
    }
}
